            if (m_firstArray[fi].Item.Hash == hash
                && key.Equals(m_firstArray[fi].Item.Key))
                return true;
            while (ei > 0)
            {
                if (m_extraArray[ei].Item.Hash == hash
                    && key.Equals(m_extraArray[ei].Item.Key))
                    return true;
