            var ei = m_firstArray[fi].Next;
            if (ei == 0) return false;
            if (m_firstArray[fi].Item.Key == hash
                && value.Equals(m_firstArray[fi].Item.Value))
                return true;
            while (ei > 0)
            {
                if (m_extraArray[ei].Item.Key == hash
                    && value.Equals(m_firstArray[fi].Item.Value))
