        public IEnumerable<Symbol> Keys
        {
            get
            {
                for (uint fi = 0; fi < m_capacity; fi++)
                {
                    var ei = m_firstArray[fi].Next;
                    if (ei == 0) continue;
                    yield return m_firstArray[fi].Item.Key;
                    while (ei > 0)
                    {
                        yield return m_extraArray[ei].Item.Key;
                        ei = m_extraArray[ei].Next;
                    }
                }
            }
        }

