                {
                    var ei = m_firstArray[fi].Next;
                    if (ei == 0) continue;
                    yield return m_firstArray[fi].Item.Value;
                    while (ei > 0)
                    {
                        yield return m_extraArray[ei].Item.Value;
                        ei = m_extraArray[ei].Next;
                    }
                }
            }
