        /// FastConcurrentSymbolDict acts as a stack for all items with the same key.
        /// </summary>
        public FastConcurrentSymbolDict(IEnumerable<KeyValuePair<Symbol, TValue>> items,
                bool stackDuplicateKeys = false)
