                    m_firstArray[fi].Item = m_extraArray[ei].Item;
                    m_extraArray[ei].Next = m_freeIndex;
                    m_extraArray[ei].Item = default(HashKey<TKey>);
                    m_freeIndex = ei;
                    --m_extraCount;
