        {
            var hash = m_hfun(key);
            var fi = ((ulong)hash) % m_capacity;
            var ei = m_firstArray[fi].Next;
            if (ei == 0)
                throw new KeyNotFoundException();
            if (m_firstArray[fi].Item.Hash == hash
                && key.Equals(m_firstArray[fi].Item.Key))
            {
                if (skip == 0) return m_firstArray[fi].Item.Value;
                --skip;
