                    var ei = m_firstArray[fi].Next;
                    if (ei == 0) continue;
                    while (ei > 0) { stack.Push(ei); ei = m_extraArray[ei].Next; }
                    var kvp = default(KeyValuePair<TKey, TValue>);
                    while (stack.Count > 0)
