                        ei = stack.Pop();
                        kvp = new KeyValuePair<TKey, TValue>(
                                m_extraArray[ei].Item.Key, m_extraArray[ei].Item.Value);
                        m_lock.Exit(); locked = false; yield return kvp; m_lock.Enter(ref locked);
