        {
            var size = (V2l)iSize.Size2d;
            return new Box2l(center - size / 2, center + size / 2);
        }

        /// <summary>
        /// Creates box from 2 points which need not be Min and Max.
        /// </summary>
        public static Box2l FromPoints(V2l p0, V2l p1)
