        public Box3d BoundingBox3d
        {
            get { return new Box3d((V3d)Min, (V3d)Max); }
        }

        #endregion

        #region ISize3d Members

        public V3d Size3d
        {
            get { return (V3d)Size; }
        }

        #endregion

